const umiSections = {
  "imports": "// npm install @metaplex-foundation/mpl-token-metadata @metaplex-foundation/mpl-toolbox @metaplex-foundation/umi @metaplex-foundation/umi-bundle-defaults\nimport {\n  createFungible,\n  mplTokenMetadata,\n} from '@metaplex-foundation/mpl-token-metadata'\nimport {\n  createTokenIfMissing,\n  findAssociatedTokenPda,\n  mintTokensTo,\n} from '@metaplex-foundation/mpl-toolbox'\nimport {\n  generateSigner,\n  keypairIdentity,\n  percentAmount,\n  some,\n} from '@metaplex-foundation/umi'\nimport { createUmi } from '@metaplex-foundation/umi-bundle-defaults'\nimport { readFileSync } from 'fs'",
  "setup": "// Initialize Umi with your RPC endpoint\nconst umi = createUmi('https://api.devnet.solana.com').use(mplTokenMetadata())\n\n// Load your wallet keypair\nconst wallet = '<your wallet file path>'\nconst secretKey = JSON.parse(readFileSync(wallet, 'utf-8'))\nconst keypair = umi.eddsa.createKeypairFromSecretKey(new Uint8Array(secretKey))\numi.use(keypairIdentity(keypair))\n\n// Generate a new mint account\nconst mint = generateSigner(umi)",
  "main": "// Step 1: Create the fungible token with metadata\nawait createFungible(umi, {\n  mint,\n  name: 'My Fungible Token',\n  symbol: 'MFT',\n  uri: 'https://example.com/my-token-metadata.json',\n  sellerFeeBasisPoints: percentAmount(0),\n  decimals: some(9),\n}).sendAndConfirm(umi)\n\n// Step 2: Mint initial supply to your wallet\nawait createTokenIfMissing(umi, {\n  mint: mint.publicKey,\n  owner: umi.identity.publicKey,\n})\n  .add(\n    mintTokensTo(umi, {\n      mint: mint.publicKey,\n      token: findAssociatedTokenPda(umi, {\n        mint: mint.publicKey,\n        owner: umi.identity.publicKey,\n      }),\n      amount: 1_000_000_000_000_000, // 1,000,000 tokens with 9 decimals\n    })\n  )\n  .sendAndConfirm(umi)",
  "output": "console.log('Token created:', mint.publicKey)\nconsole.log('Metadata and mint account initialized')\nconsole.log('Initial supply minted to:', umi.identity.publicKey)",
  "full": "// [IMPORTS]\n// npm install @metaplex-foundation/mpl-token-metadata @metaplex-foundation/mpl-toolbox @metaplex-foundation/umi @metaplex-foundation/umi-bundle-defaults\nimport {\n  createFungible,\n  mplTokenMetadata,\n} from '@metaplex-foundation/mpl-token-metadata'\nimport {\n  createTokenIfMissing,\n  findAssociatedTokenPda,\n  mintTokensTo,\n} from '@metaplex-foundation/mpl-toolbox'\nimport {\n  generateSigner,\n  keypairIdentity,\n  percentAmount,\n  some,\n} from '@metaplex-foundation/umi'\nimport { createUmi } from '@metaplex-foundation/umi-bundle-defaults'\nimport { readFileSync } from 'fs'\n// [/IMPORTS]\n\n// [SETUP]\n// Initialize Umi with your RPC endpoint\nconst umi = createUmi('https://api.devnet.solana.com').use(mplTokenMetadata())\n\n// Load your wallet keypair\nconst wallet = '<your wallet file path>'\nconst secretKey = JSON.parse(readFileSync(wallet, 'utf-8'))\nconst keypair = umi.eddsa.createKeypairFromSecretKey(new Uint8Array(secretKey))\numi.use(keypairIdentity(keypair))\n\n// Generate a new mint account\nconst mint = generateSigner(umi)\n// [/SETUP]\n\n// [MAIN]\n// Step 1: Create the fungible token with metadata\nawait createFungible(umi, {\n  mint,\n  name: 'My Fungible Token',\n  symbol: 'MFT',\n  uri: 'https://example.com/my-token-metadata.json',\n  sellerFeeBasisPoints: percentAmount(0),\n  decimals: some(9),\n}).sendAndConfirm(umi)\n\n// Step 2: Mint initial supply to your wallet\nawait createTokenIfMissing(umi, {\n  mint: mint.publicKey,\n  owner: umi.identity.publicKey,\n})\n  .add(\n    mintTokensTo(umi, {\n      mint: mint.publicKey,\n      token: findAssociatedTokenPda(umi, {\n        mint: mint.publicKey,\n        owner: umi.identity.publicKey,\n      }),\n      amount: 1_000_000_000_000_000, // 1,000,000 tokens with 9 decimals\n    })\n  )\n  .sendAndConfirm(umi)\n// [/MAIN]\n\n// [OUTPUT]\nconsole.log('Token created:', mint.publicKey)\nconsole.log('Metadata and mint account initialized')\nconsole.log('Initial supply minted to:', umi.identity.publicKey)\n// [/OUTPUT]\n"
}

